import React, { useState, useEffect } from "react";
import { fetchData } from "../api/fetchApi";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import styles from "./Table.css";

const Tabular = ({ data: { Arunachal, Assam, Manipur, Meghalaya, Mizoram, Nagaland, Tripura }, country }) => {

    const [fetchedState, setFetchedState] = useState([])

    useEffect(() => {
        const fetchAPIState = async () => {
            const dailyData = await fetchData()
            setFetchedState(dailyData)
        }
        fetchAPIState()
    }, [])

    return (
        <TableContainer component={Paper}>
            <Table className={styles.container} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>State</TableCell>
                        <TableCell className="alert alert-warning" align="right">Cases</TableCell>
                        <TableCell className="alert alert-success" align="right">Recovered</TableCell>
                        <TableCell className="alert alert-danger" align="right">Deaths</TableCell>

                    </TableRow>
                </TableHead>
                <TableBody>
                    {fetchedState.slice(7).map(({ country, cases, todayCases, deaths, todayDeaths, recovered, active, critical }, i) => (
                        <TableRow key={i}>
                            <TableCell component="th" scope="row">
                                {country}
                            </TableCell>
                            <TableCell className="alert alert-warning" align="right">{cases}</TableCell>
                            <TableCell className="alert alert-success" align="right">{recovered}</TableCell>
                            <TableCell className="alert alert-danger" align="right">{deaths}</TableCell>
                            {/* <TableCell align="right">{todayCases}</TableCell>
              <TableCell align="right">{todayDeaths}</TableCell> */}

                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    )
}

export default Tabular
